  if (regDate === "") {
      return undefined
  } else {
      var regYear = parseInt(regDate.slice(6,10));
	  var regDay = parseInt(regDate.slice(0,2));
      var regMonth = parseInt(regDate.slice(3,5));
      var regJSDate = new Date(regYear,regMonth-1,regDay);
      var today = new Date();
      var daysSinceReg = parseInt((today - regJSDate) / 86400000);
      return daysSinceReg
  }
}

